import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getDraftEstimates } from '../../api/estimateRequest';
import './DashboardPages.css';
import './EstimateInquiry.css';

interface DraftItem {
  estimateNo: string;
  companyName: string;
  contactPerson: string;
  requestDate: string;
  quantity: number;
  statusText: string;
  status: number;
  project: string;
  tempEstimateNo: string;
  writerID: string;
  managerID?: string;
  managerName?: string;
}

const TemporaryStoragePage: React.FC = () => {
  const navigate = useNavigate();
  const [items, setItems] = useState<DraftItem[]>([]);
  const [loading, setLoading] = useState(false);
  const [currentUser, setCurrentUser] = useState<any>(null);
  const [selectedCustomer, setSelectedCustomer] = useState<any>(null);

  // ÌïÑÌÑ∞ ÏÉÅÌÉú
  const [searchKeyword, setSearchKeyword] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');

  // ÌéòÏù¥Ïßï ÏÉÅÌÉú
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [totalCount, setTotalCount] = useState(0);
  const [pageSize] = useState(10);

  // Îç∞Ïù¥ÌÑ∞ Ï°∞Ìöå Ìï®Ïàò
  const fetchData = async (page: number = 1) => {
    setLoading(true);
    try {
      const params = {
        searchKeyword: searchKeyword || undefined,
        startDate: startDate || undefined,
        endDate: endDate || undefined,
        page,
        pageSize,
        isDescending: true,
      };

      // ÌòÑÏû¨ ÏÇ¨Ïö©Ïûê ID Í∞ÄÏ†∏Ïò§Í∏∞
      const currentUserId = currentUser?.userId || 'defaultUser';

      // ÏÑ†ÌÉùÎêú Í≥†Í∞ù ID Í∞ÄÏ†∏Ïò§Í∏∞ (ÏûàÏúºÎ©¥)
      const customerId = selectedCustomer?.userId;

      const response = await getDraftEstimates(params, currentUserId, customerId);
      setItems(response.items);
      setCurrentPage(response.currentPage);
      setTotalPages(response.totalPages);
      setTotalCount(response.totalCount);
    } catch (error) {
      console.error('ÏûÑÏãúÏ†ÄÏû• Î™©Î°ù Ï°∞Ìöå Ïã§Ìå®:', error);
      alert('ÏûÑÏãúÏ†ÄÏû• Î™©Î°ù Ï°∞ÌöåÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    } finally {
      setLoading(false);
    }
  };

  // Ï¥àÍ∏∞ Îç∞Ïù¥ÌÑ∞ Î°úÎìú
  useEffect(() => {
    // ÌòÑÏû¨ ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
    const userStr = localStorage.getItem('user');
    if (userStr) {
      setCurrentUser(JSON.parse(userStr));
    }

    // ÏÑ†ÌÉùÎêú Í≥†Í∞ù Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞ (ÏûÑÏãúÏ†ÄÏû•Ïö©)
    const selectedCustomerStr = localStorage.getItem('selectedCustomerForTempStorage');
    if (selectedCustomerStr) {
      setSelectedCustomer(JSON.parse(selectedCustomerStr));
      // ÏÇ¨Ïö© ÌõÑ Ï†úÍ±∞
      localStorage.removeItem('selectedCustomerForTempStorage');
    }

    // Í∏∞Î≥∏ ÎÇ†Ïßú Î≤îÏúÑ ÏÑ§Ï†ï (ÏµúÍ∑º 3Í∞úÏõî)
    const today = new Date();
    const threeMonthsAgo = new Date();
    threeMonthsAgo.setMonth(today.getMonth() - 3);

    setStartDate(threeMonthsAgo.toISOString().split('T')[0]);
    setEndDate(today.toISOString().split('T')[0]);
  }, []);

  // ÎÇ†ÏßúÍ∞Ä ÏÑ§Ï†ïÎêòÎ©¥ Îç∞Ïù¥ÌÑ∞ Ï°∞Ìöå
  useEffect(() => {
    if (startDate && endDate) {
      fetchData(1);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [startDate, endDate]);

  // Í≤ÄÏÉâ Î≤ÑÌäº ÌÅ¥Î¶≠
  const handleSearch = () => {
    setCurrentPage(1);
    fetchData(1);
  };

  // ÌéòÏù¥ÏßÄ Î≥ÄÍ≤Ω
  const handlePageChange = (page: number) => {
    fetchData(page);
  };

  // TempEstimateNoÏóêÏÑú ÎÇ†Ïßú Ï∂îÏ∂ú (YYYY.MM.DD)
  const extractDateFromTempEstimateNo = (tempEstimateNo: string): string => {
    const match = tempEstimateNo.match(/TEMP(\d{4})(\d{2})(\d{2})/);
    if (match) {
      const year = match[1];
      const month = match[2];
      const day = match[3];
      return `${year}.${month}.${day}`;
    }
    return tempEstimateNo;
  };

  // ÏöîÏ≤≠ÏùºÏûê YYYY.MM.DD Ìè¨Îß∑ (ÏãúÍ∞Ñ Ï†úÍ±∞)
  const formatDateYmd = (dateString?: string): string => {
    if (!dateString) return '';
    const head = dateString.slice(0, 10);
    return head.replace(/[-\/]/g, '.');
  };

  // Ìñâ ÌÅ¥Î¶≠ Ìï∏Îì§Îü¨ (Í≤¨Ï†Å ÏûëÏÑ± ÌéòÏù¥ÏßÄÎ°ú Ïù¥ÎèôÌïòÏó¨ Î∂àÎü¨Ïò§Í∏∞)
  const handleRowClick = (item: DraftItem) => {
    // ÌòÑÏû¨ ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
    let user = currentUser;
    if (!user) {
      const userStr = localStorage.getItem('user');
      if (userStr) {
        user = JSON.parse(userStr);
        setCurrentUser(user);
      }
    }

    // ÏàòÏ†ï Í∞ÄÎä• Ïó¨Î∂Ä ÌôïÏù∏: ÌòÑÏû¨ ÏÇ¨Ïö©ÏûêÍ∞Ä ÏûëÏÑ±ÏûêÏù¥Í≥† ÏÉÅÌÉúÍ∞Ä 'ÏûÑÏãúÏ†ÄÏû•'(1) ÎòêÎäî 'Í≤¨Ï†ÅÏöîÏ≤≠'(2)Ïù∏ Í≤ΩÏö∞Îßå ÏàòÏ†ï Í∞ÄÎä•
    const canEdit = (item.status === 1 || item.status === 2) && user && item.writerID === user.userId;
    const readonly = canEdit ? 'false' : 'true';

    // tempEstimateNoÏôÄ readonly ÏÉÅÌÉúÎ•º ÏøºÎ¶¨ ÌååÎùºÎØ∏ÌÑ∞Î°ú Ï†ÑÎã¨
    navigate(`/estimate-request/new?load=${item.tempEstimateNo}&readonly=${readonly}`);
  };

  return (
    <div className="page">
      <div className="page-header">
        <div className="header-left">
          <button className="back-btn" onClick={() => navigate(-1)}>
            &lt;
          </button>
          <h1>üìÅ ÏûÑÏãúÏ†ÄÏû•Ìï®</h1>
          {selectedCustomer && (
            <span style={{ marginLeft: '10px', fontSize: '16px', color: '#666' }}>
              - {selectedCustomer.companyName}
            </span>
          )}
        </div>
      </div>

      {/* Í≤ÄÏÉâ ÏÑπÏÖò */}
      <div className="search-section">
        <div className="search-row">
          <div className="search-field">
            <input
              type="text"
              placeholder="Í≤ÄÏÉâÏñ¥ ÏûÖÎ†• (ÌîÑÎ°úÏ†ùÌä∏Î™Ö, ÌöåÏÇ¨Î™Ö Îì±)"
              value={searchKeyword}
              onChange={(e) => setSearchKeyword(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
            />
          </div>

          <div className="date-range">
            <span>Í∏∞Í∞Ñ:</span>
            <input
              type="date"
              value={startDate}
              onChange={(e) => setStartDate(e.target.value)}
            />
            <span>~</span>
            <input
              type="date"
              value={endDate}
              onChange={(e) => setEndDate(e.target.value)}
            />
          </div>

          <button className="action-btn" onClick={handleSearch}>
            Í≤ÄÏÉâ
          </button>
        </div>
      </div>

      {/* Í≤∞Í≥º Ìó§Îçî */}
      <div className="results-header">
        <div className="results-info">
          Ï¥ù {totalCount}Í∞úÏùò ÏûÑÏãúÏ†ÄÏû• Ìï≠Î™©Ïù¥ ÏûàÏäµÎãàÎã§.
        </div>
      </div>

      {/* ÌÖåÏù¥Î∏î */}
      <div className="table-container">
        <table className="inquiry-table">
          <thead>
            <tr>
              <th>Í≤¨Ï†ÅÎ≤àÌò∏</th>
              <th>ÌöåÏÇ¨Î™Ö</th>
              <th>Îã¥ÎãπÏûê</th>
              <th>ÏöîÏ≤≠ÏùºÏûê</th>
              <th>ÏÉÅÌÉú</th>
              <th>ÌîÑÎ°úÏ†ùÌä∏Î™Ö</th>
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr>
                <td colSpan={6} className="loading">Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</td>
              </tr>
            ) : items.length === 0 ? (
              <tr>
                <td colSpan={6} className="no-data">ÏûÑÏãúÏ†ÄÏû•Îêú Í≤¨Ï†ÅÏù¥ ÏóÜÏäµÎãàÎã§.</td>
              </tr>
            ) : (
              items.map((item) => (
                <tr 
                  key={item.tempEstimateNo} 
                  className="clickable-row"
                  onClick={() => handleRowClick(item)}
                >
                  <td>{item.estimateNo}</td>
                  <td>{item.companyName}</td>
                  <td>{item.managerName || 'ÎØ∏ÏßÄÏ†ï'}</td>
                  <td>{item.requestDate ? formatDateYmd(item.requestDate) : extractDateFromTempEstimateNo(item.tempEstimateNo)}</td>
                  <td>
                    <span className={`status-badge status-${item.status}`}>
                      {item.statusText}
                    </span>
                  </td>
                  <td>{item.project || '-'}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      {/* ÌéòÏù¥Ïßï */}
      {totalPages > 1 && (
        <div className="pagination">
          <button 
            onClick={() => handlePageChange(1)}
            disabled={currentPage === 1}
          >
            Ï≤òÏùå
          </button>
          <button 
            onClick={() => handlePageChange(currentPage - 1)}
            disabled={currentPage === 1}
          >
            Ïù¥Ï†Ñ
          </button>

          {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
            const page = Math.max(1, Math.min(totalPages - 4, currentPage - 2)) + i;
            return (
              <button
                key={page}
                onClick={() => handlePageChange(page)}
                className={currentPage === page ? 'active' : ''}
              >
                {page}
              </button>
            );
          })}

          <button 
            onClick={() => handlePageChange(currentPage + 1)}
            disabled={currentPage === totalPages}
          >
            Îã§Ïùå
          </button>
          <button 
            onClick={() => handlePageChange(totalPages)}
            disabled={currentPage === totalPages}
          >
            ÎßàÏßÄÎßâ
          </button>
        </div>
      )}
    </div>
  );
};

export default TemporaryStoragePage;
