import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './DashboardPages.css';
import './StaffManagement.css';
import { getStaff, createStaff, searchStaff, UserListResponseDto, CreateUserDto } from '../../api/userManagement';

interface Staff {
  userID: string;
  name: string;
  department: string;
  email: string;
}

const StaffManagementPage: React.FC = () => {
  const navigate = useNavigate();
  const [staffMembers, setStaffMembers] = useState<Staff[]>([]);
  const [filteredStaff, setFilteredStaff] = useState<Staff[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [showAddModal, setShowAddModal] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Îã¥ÎãπÏûê Î™©Î°ù Î°úÎìú
  useEffect(() => {
    loadStaff();
  }, []);

  const loadStaff = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await getStaff();
      const staffData: Staff[] = data.map(staff => ({
        userID: staff.userID,
        name: staff.name,
        department: staff.department,
        email: staff.email
      }));
      setStaffMembers(staffData);
      setFilteredStaff(staffData);
    } catch (err) {
      console.error('Îã¥ÎãπÏûê Î™©Î°ù Î°úÎìú Ïã§Ìå®:', err);
      setError('Îã¥ÎãπÏûê Î™©Î°ùÏùÑ Î∂àÎü¨Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    } finally {
      setLoading(false);
    }
  };

  // Í≤ÄÏÉâ Í∏∞Îä•
  useEffect(() => {
    if (searchTerm.trim() === '') {
      setFilteredStaff(staffMembers);
    } else {
      const filtered = staffMembers.filter(staff =>
        staff.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        staff.department.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredStaff(filtered);
    }
  }, [searchTerm, staffMembers]);

  const handleStaffClick = (staffId: string) => {
    navigate(`/staff-detail/${staffId}`);
  };

  const handleAddStaff = () => {
    setShowAddModal(true);
