  const downloadFile = async (filePath: string, fileName: string) => {
    try {
      // üîë ÌååÏùº Îã§Ïö¥Î°úÎìú API ÏàòÏ†ï - ÏÉàÎ°ú Ï∂îÍ∞ÄÎêú API ÏÇ¨Ïö©
      const response = await fetch(`http://localhost:5135/api/estimate/attachments/download?filePath=${encodeURIComponent(filePath)}`);
      if (response.ok) {
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = fileName;
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
        document.body.removeChild(a);
      } else {
        // üîë Îã§Ïö¥Î°úÎìú Ïã§Ìå® Ïãú ÏÉÅÏÑ∏ ÏóêÎü¨ Ï†ïÎ≥¥ ÌëúÏãú
        const errorText = await response.text();
