      const response = await getEstimateDetail(tempEstimateNo, currentUser?.userId || 'admin'); // Ïã§Ï†ú ÏÇ¨Ïö©Ïûê ID ÏÇ¨Ïö©
      const data = response;

      //console.log('Í≤¨Ï†Å ÏÉÅÏÑ∏ Îç∞Ïù¥ÌÑ∞:', data);


      // ÌîÑÎ°úÏ†ùÌä∏Î™Ö ÏÑ§Ï†ï
      if (data.estimateSheet && data.estimateSheet.project) {
        setProjectName(data.estimateSheet.project);
      }

      // ÌòÑÏû¨ ÏÉÅÌÉú ÏÑ§Ï†ï + Í≤¨Ï†ÅÏãúÏûë ÌÜ†Í∏Ä
      if (data.estimateSheet && data.estimateSheet.statusText) {
