                qn: { max: tag.qnMax || 0, normal: tag.qnNor || 0, min: tag.qnMin || 0, unit: tag.qnUnit || '' },
                pressureUnit: tag.pressureUnit || '',
                temperatureUnit: tag.temperatureUnit || ''
              },
              body: {
                type: bodyTypeName, // Body Type Ïù¥Î¶Ñ ÏÑ§Ï†ï
                typeCode: req.valveType || '',
                size: tag.bodySize || '',
