              },
              accessory: {
                positioner: { type: tag.positionerType || '', exists: tag.isPositioner || false },
                explosionProof: tag.explosionProof || '',
                transmitter: { type: tag.transmitterType || '', exists: !!tag.transmitterType },
                solenoidValve: tag.isSolenoid || false,
