              isDensity: tag.isDensity || false,
              isHW: tag.isHW || false
            });
          });
        });
        // sheetNo Í∏∞Ï§ÄÏúºÎ°ú Ïò§Î¶ÑÏ∞®Ïàú Ï†ïÎ†¨
valvesData.sort((a, b) => (a.sheetNo || 0) - (b.sheetNo || 0));

// Ï†ïÎ†¨Îêú Í≤∞Í≥ºÎ°ú ÏÉÅÌÉú Î∞òÏòÅ
setValves(valvesData);



