import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Sidebar.css';

interface MenuItem {
  id: string;
  label: string;
  icon: string;
  path: string;
  roles: number[]; // 1: Admin, 2: Staff, 3: Customer
}

const menuItems: MenuItem[] = [
  {
    id: 'statistics',
    label: 'ÌÜµÍ≥Ñ Î∂ÑÏÑù',
    icon: 'üìä',
    path: '/statistics',
    roles: [1, 2] // Admin, Staff
  },
  {
    id: 'customer-management',
    label: 'Í≥†Í∞ù Í¥ÄÎ¶¨',
    icon: 'üë•',
    path: '/customer-management',
    roles: [1, 2] // Admin, Staff
  },
  {
    id: 'staff-management',
    label: 'Îã¥ÎãπÏûê Í¥ÄÎ¶¨',
    icon: 'üë®‚Äçüíº',
    path: '/staff-management',
    roles: [1] // Admin only
  },
  {
    id: 'membership-requests',
    label: 'ÌöåÏõêÍ∞ÄÏûÖ ÏöîÏ≤≠',
    icon: 'üîî',
    path: '/membership-requests',
    roles: [1] // Admin only
  },
  {
    id: 'estimate-request',
    label: 'Í≤¨Ï†ÅÏöîÏ≤≠',
    icon: 'üìù',
    path: '/estimate-request',
    roles: [1, 2, 3] // All roles
  },
  {
    id: 'estimate-inquiry',
    label: 'Í≤¨Ï†ÅÏöîÏ≤≠ Ï°∞Ìöå',
    icon: 'üîç',
    path: '/estimate-inquiry',
    roles: [1, 2, 3] // All roles
  },
  {
    id: 'estimate-management',
    label: 'Í≤¨Ï†ÅÏöîÏ≤≠ Í¥ÄÎ¶¨',
    icon: '‚öôÔ∏è',
    path: '/estimate-management',
    roles: [1, 2] // Admin, Staff
  },
  {
    id: 'temporary-storage',
    label: 'ÏûÑÏãúÏ†ÄÏû•Ìï®',
    icon: 'üìÅ',
    path: '/temporary-storage',
    roles: [1, 2, 3] // All roles
  },
  {
    id: 'accessory-management',
    label: 'Ï†úÌíàÏΩîÎìú Í¥ÄÎ¶¨',
    icon: 'üîß',
    path: '/accessory-management',
    roles: [1, 2] // Admin, Staff
  }
];

interface SidebarProps {
  userRole: number;
}

const Sidebar: React.FC<SidebarProps> = ({ userRole }) => {
  const location = useLocation();

  // ÏÇ¨Ïö©Ïûê Ïó≠Ìï†Ïóê ÎßûÎäî Î©îÎâ¥Îßå ÌïÑÌÑ∞ÎßÅ
  const filteredMenuItems = menuItems.filter(item => 
    item.roles.includes(userRole)
  );

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <h2 className="logo">PROVAL PROVAL Co.,LTD.</h2>
      </div>

      <nav className="sidebar-nav">
        {filteredMenuItems.map((item) => {
          const isActive = location.pathname === item.path;

          return (
            <Link
              key={item.id}
              to={item.path}
              className={`sidebar-menu-item ${isActive ? 'active' : ''}`}
            >
              <span className="menu-icon">{item.icon}</span>
              <span className="menu-label">{item.label}</span>
            </Link>
          );
        })}
      </nav>

      <div className="sidebar-footer">
        <div className="language-toggle">
          <span>ÏòÅÏñ¥ Î≤àÏó≠</span>
          <label className="toggle-switch">
            <input type="checkbox" />
            <span className="toggle-slider"></span>
          </label>
        </div>
        <div className="logout-section">
          <button 
            onClick={() => {
              localStorage.removeItem('token');
              localStorage.removeItem('user');
              window.location.href = '/login';
            }}
            className="logout-button"
          >
            üö™ Î°úÍ∑∏ÏïÑÏõÉ
          </button>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
