  if (listKey === 'types') {
    setTypes(prev => {
      const arr = [...prev];
      const [moved] = arr.splice(index, 1);
      arr.splice(dropIndex, 0, moved);
      return arr;
    });
  } else {
    setValves(prev => {
      const arr = [...prev];
      const [moved] = arr.splice(index, 1);
      arr.splice(dropIndex, 0, moved);
