      }
    });

    // RatingÍ≥º ÎèôÏùºÌïú { code, name } ÌòïÌÉúÎ°ú Î∞òÌôòÌï©ÎãàÎã§.
    const units = Array.from(unitMap, ([code, name]) => ({ code, name }));
    console.log('üîç ÏÉùÏÑ±Îêú bodySizeUnits:', units);
    console.log('üîç bodySizeUnits Í∏∏Ïù¥:', units.length);

    const sortedUnits = units.sort((a, b) => customSort(a.name, b.name));
