                    actionType: '',
                    actionTypeCode: '', // Code ê°’ ì¶”ê°€
                    series: '',
                    seriesCode: '', // Code ê°’ ì¶”ê°€
                    size: '',
                    sizeCode: '', // Code ê°’ ì¶”ê°€
                    hw: '',
                    hwCode: '' // Code ê°’ ì¶”ê°€
                  });

  // ìƒíƒœ ë° í”„ë¡œì íŠ¸ ì •ë³´
  const [projectName, setProjectName] = useState<string>('');

  // BodyValveList ê°€ì ¸ì˜¤ê¸°
  const fetchBodyValveList = async () => {
