import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { getEstimateInquiry, EstimateInquiryRequest, EstimateInquiryItem, statusOptions } from '../../api/estimateInquiry';
import './DashboardPages.css';
import './EstimateInquiry.css';

const ExistingEstimateReInquiryPage: React.FC = () => {
  const navigate = useNavigate();
  const [items, setItems] = useState<EstimateInquiryItem[]>([]);
  const [loading, setLoading] = useState(false);
  const [currentUser, setCurrentUser] = useState<any>(null);
  const searchTimeout = useRef<NodeJS.Timeout | null>(null);

  // ÌïÑÌÑ∞ ÏÉÅÌÉú
  const [searchKeyword, setSearchKeyword] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [isDescending, setIsDescending] = useState(true);
  const [statusFilter, setStatusFilter] = useState<string>('');

  // ÌéòÏù¥Ïßï ÏÉÅÌÉú
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [totalCount, setTotalCount] = useState(0);
  const [pageSize] = useState(10);

  // Îç∞Ïù¥ÌÑ∞ Ï°∞Ìöå Ìï®Ïàò (Îß§Í∞úÎ≥ÄÏàòÎ°ú ÌòÑÏû¨ ÏÉÅÌÉúÎ•º Î∞õÏïÑÏÑú Ï≤òÎ¶¨)
  const fetchData = async (page: number = 1, overrideParams: Partial<EstimateInquiryRequest> = {}) => {
    setLoading(true);
    try {
      // Îß§Î≤à Ìò∏Ï∂úÌï† ÎïåÎßàÎã§ ÌòÑÏû¨ ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥Î•º Îã§Ïãú Í∞ÄÏ†∏Ïò§Í∏∞
      let user = currentUser;
      if (!user) {
        const userStr = localStorage.getItem('user');
        if (userStr) {
          user = JSON.parse(userStr);
          setCurrentUser(user);
        }
      }

      // ÎîîÎ≤ÑÍπÖ: ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ ÌôïÏù∏
      console.log('ÌòÑÏû¨ ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥:', user);
      console.log('ÏÇ¨Ïö©Ïûê roleId:', user?.roleId);
      console.log('ÏÇ¨Ïö©Ïûê userId:', user?.userId);

      const params: EstimateInquiryRequest = {
        searchKeyword: searchKeyword || undefined,
        startDate: startDate || undefined,
        endDate: endDate || undefined,
        status: statusFilter ? Number(statusFilter) : undefined,
        page,
        pageSize,
        isDescending,
        ...overrideParams, // Îß§Í∞úÎ≥ÄÏàòÎ°ú Ï†ÑÎã¨Îêú Í∞íÏù¥ Ïö∞ÏÑ†
      };

      // Í≥†Í∞ù(roleIDÍ∞Ä 3)Ïù∏ Í≤ΩÏö∞ ÏûêÏã†Ïùò UserIDÎßå Ï°∞Ìöå
      if (user && user.roleId === 3) {
        params.customerID = user.userId;
        console.log('Í≥†Í∞ù Í∂åÌïúÏúºÎ°ú Ï°∞Ìöå - CustomerID(UserID):', user.userId);
      }

      console.log('API ÏöîÏ≤≠ ÌååÎùºÎØ∏ÌÑ∞:', params); // ÎîîÎ≤ÑÍπÖÏö©
      const response = await getEstimateInquiry(params);
      setItems(response.items);
      setCurrentPage(response.currentPage);
      setTotalPages(response.totalPages);
      setTotalCount(response.totalCount);
    } catch (error) {
      console.error('Îç∞Ïù¥ÌÑ∞ Ï°∞Ìöå Ïã§Ìå®:', error);
      alert('Îç∞Ïù¥ÌÑ∞ Ï°∞ÌöåÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    } finally {
      setLoading(false);
    }
  };

  // Ï¥àÍ∏∞ Îç∞Ïù¥ÌÑ∞ Î°úÎìú
  useEffect(() => {
    // ÌòÑÏû¨ ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
    const userStr = localStorage.getItem('user');
    if (userStr) {
      setCurrentUser(JSON.parse(userStr));
    }

    // Í∏∞Î≥∏ ÎÇ†Ïßú Î≤îÏúÑ ÏÑ§Ï†ï (ÏµúÍ∑º 1Í∞úÏõî)
    const today = new Date();
    const oneMonthAgo = new Date();
    oneMonthAgo.setMonth(today.getMonth() - 1);

    setStartDate(oneMonthAgo.toISOString().split('T')[0]);
    setEndDate(today.toISOString().split('T')[0]);
  }, []);

  // ÎÇ†ÏßúÍ∞Ä ÏÑ§Ï†ïÎêòÎ©¥ Îç∞Ïù¥ÌÑ∞ Ï°∞Ìöå
  useEffect(() => {
    if (startDate && endDate) {
      fetchData(1);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [startDate, endDate]);

  // Í≤ÄÏÉâ Î≤ÑÌäº ÌÅ¥Î¶≠
  const handleSearch = () => {
    setCurrentPage(1);
    fetchData(1);
  };

  // ÌéòÏù¥ÏßÄ Î≥ÄÍ≤Ω
  const handlePageChange = (page: number) => {
    fetchData(page);
  };

  // Ï†ïÎ†¨ ÌÜ†Í∏Ä
  const handleSortToggle = () => {
    const newIsDescending = !isDescending;
    setIsDescending(newIsDescending);
    setCurrentPage(1);
    setTimeout(() => {
      fetchData(1, { isDescending: newIsDescending });
    }, 100);
  };

  // TempEstimateNoÏóêÏÑú ÎÇ†Ïßú Ï∂îÏ∂ú Î∞è Ìè¨Îß∑ÌåÖ
  const extractDateFromTempEstimateNo = (tempEstimateNo: string): string => {
    const match = tempEstimateNo.match(/TEMP(\d{4})(\d{2})(\d{2})/);
    if (match) {
      const year = match[1];
      const month = match[2];
      const day = match[3];
      return `${year}.${month}.${day}`;
    }
    return tempEstimateNo; // Îß§Ïπ≠ÎêòÏßÄ ÏïäÏúºÎ©¥ ÏõêÎ≥∏ Î∞òÌôò (ÌòπÏùÄ Îπà Î¨∏ÏûêÏó¥ Îì±)
  };

  // ÏöîÏ≤≠ÏùºÏûê YYYY.MM.DD Ìè¨Îß∑ (ÏãúÍ∞Ñ Ï†úÍ±∞)
  const formatDateYmd = (dateString?: string): string => {
    if (!dateString) return '';
    const head = dateString.slice(0, 10); // YYYY-MM-DD ÎòêÎäî YYYY.MM.DD
    return head.replace(/[-/]/g, '.');
  };

  // Ìñâ ÌÅ¥Î¶≠ Ìï∏Îì§Îü¨ (Í∏∞Ï°¥ Í≤¨Ï†ÅÏóêÏÑú ÏÉàÎ°úÏö¥ Í≤¨Ï†Å ÏÉùÏÑ±)
  const handleRowClick = async (item: EstimateInquiryItem) => {
    try {
      // ÌòÑÏû¨ ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥Í∞Ä ÏóÜÏúºÎ©¥ Îã§Ïãú Í∞ÄÏ†∏Ïò§Í∏∞
      let user = currentUser;
      if (!user) {
        const userStr = localStorage.getItem('user');
        if (userStr) {
          user = JSON.parse(userStr);
          setCurrentUser(user);
        }
      }

      if (!user) {
        alert('ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.');
        return;
      }

      console.log('Í∏∞Ï°¥ Í≤¨Ï†ÅÏóêÏÑú ÏÉàÎ°úÏö¥ Í≤¨Ï†Å ÏÉùÏÑ± ÏãúÏûë:', item);
      console.log('Í∏∞Ï°¥ Í≤¨Ï†Å Î≤àÌò∏:', item.tempEstimateNo);
      console.log('ÌòÑÏû¨ ÏÇ¨Ïö©Ïûê:', user);

      // ÏÉàÎ°úÏö¥ Í≤¨Ï†Å ÏÉùÏÑ± API Ìò∏Ï∂ú
      const apiUrl = `http://192.168.0.14:5135/api/estimate/sheets/reinquiry?currentUserId=${user.userId}&existingEstimateNo=${item.tempEstimateNo}`;
      console.log('üîç API Ìò∏Ï∂ú URL:', apiUrl);

      const response = await fetch(apiUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          Project: item.project || '',
          CustomerRequirement: item.customerRequirement || ''
        })
      });

      if (!response.ok) {
        throw new Error('ÏÉàÎ°úÏö¥ Í≤¨Ï†Å ÏÉùÏÑ±Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }

      const newTempEstimateNo = await response.text();
      console.log('ÏÉàÎ°úÏö¥ Í≤¨Ï†Å Î≤àÌò∏ ÏÉùÏÑ±Îê®:', newTempEstimateNo);

      // ÏÉàÎ°úÏö¥ Í≤¨Ï†Å ÏöîÏ≤≠ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
      navigate(`/estimate-request/new?load=${newTempEstimateNo}&readonly=false`);

    } catch (error) {
      console.error('Í∏∞Ï°¥ Í≤¨Ï†ÅÏóêÏÑú ÏÉàÎ°úÏö¥ Í≤¨Ï†Å ÏÉùÏÑ± Ïã§Ìå®:', error);
      alert('Í∏∞Ï°¥ Í≤¨Ï†ÅÏóêÏÑú ÏÉàÎ°úÏö¥ Í≤¨Ï†ÅÏùÑ ÏÉùÏÑ±ÌïòÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    }
  };

  // Ïª¥Ìè¨ÎÑåÌä∏ Ïñ∏ÎßàÏö¥Ìä∏ Ïãú ÌÉÄÏù¥Î®∏ Ï†ïÎ¶¨
  useEffect(() => {
    return () => {
      if (searchTimeout.current) {
        clearTimeout(searchTimeout.current);
      }
    };
  }, []);

  return (
    <div className="dashboard-page">
      <div className="page-header">
        <button className="back-btn" onClick={() => navigate(-1)}>
          ‚Üê Í∏∞Ï°¥Í≤¨Ï†ÅÏû¨Î¨∏Ïùò
        </button>
        <h1>Í∏∞Ï°¥Í≤¨Ï†ÅÏû¨Î¨∏Ïùò</h1>
        <p className="page-description">Í∏∞Ï°¥ Í≤¨Ï†ÅÏùÑ ÌÅ¥Î¶≠ÌïòÎ©¥ ÏÉàÎ°úÏö¥ Í≤¨Ï†Å ÏöîÏ≤≠Ïù¥ ÏÉùÏÑ±Îê©ÎãàÎã§.</p>
      </div>

      {/* Í≤ÄÏÉâ ÌïÑÌÑ∞ ÏòÅÏó≠ */}
      <div className="search-section">
        <div className="search-row">
          <div className="date-range">
            <span>Îì±Î°ùÍ∏∞Í∞Ñ</span>
            <input
              type="date"
              value={startDate}
              onChange={(e) => {
                const newStartDate = e.target.value;
                setStartDate(newStartDate);
                setCurrentPage(1);
                // Ï¶âÏãú Í≤ÄÏÉâ (ÎÇ†Ïßú ÌïòÎÇòÎßå ÏûÖÎ†•Ìï¥ÎèÑ Í≤ÄÏÉâ)
                setTimeout(() => {
                  fetchData(1, { 
                    startDate: newStartDate || undefined 
                  });
                }, 100);
              }}
            />
            <span>~</span>
            <input
              type="date"
              value={endDate}
              onChange={(e) => {
                const newEndDate = e.target.value;
                setEndDate(newEndDate);
                setCurrentPage(1);
                // Ï¶âÏãú Í≤ÄÏÉâ (ÎÇ†Ïßú ÌïòÎÇòÎßå ÏûÖÎ†•Ìï¥ÎèÑ Í≤ÄÏÉâ)
                setTimeout(() => {
                  fetchData(1, { 
                    endDate: newEndDate || undefined 
                  });
                }, 100);
              }}
            />
          </div>

          <div className="status-filter">
            <span>ÏßÑÌñâÏÉÅÌÉú</span>
            <select
              value={statusFilter}
              onChange={(e) => {
                const newValue = e.target.value;
                setStatusFilter(newValue);
                setCurrentPage(1);
                setTimeout(() => {
                  fetchData(1, { status: newValue ? Number(newValue) : undefined });
                }, 100);
              }}
            >
              {statusOptions.map((opt) => (
                <option key={opt.value} value={opt.value}>{opt.label}</option>
              ))}
            </select>
          </div>

          <div className="search-field">
            <input
              type="text"
              placeholder="Í≤ÄÏÉâ (Í≤¨Ï†ÅÎ≤àÌò∏, ÌöåÏÇ¨Î™Ö, ÌîÑÎ°úÏ†ùÌä∏Î™Ö)"
              value={searchKeyword}
              onChange={(e) => {
                const newKeyword = e.target.value;
                setSearchKeyword(newKeyword);
                // ÏûÖÎ†• ÌõÑ 500ms ÌõÑ ÏûêÎèô Í≤ÄÏÉâ (ÎîîÎ∞îÏö¥Ïã±)
                if (searchTimeout.current) {
                  clearTimeout(searchTimeout.current);
                }
                searchTimeout.current = setTimeout(() => {
                  setCurrentPage(1);
                  fetchData(1, { searchKeyword: newKeyword || undefined });
                }, 500);
              }}
              onKeyPress={(e) => {
                if (e.key === 'Enter') {
                  if (searchTimeout.current) {
                    clearTimeout(searchTimeout.current);
                  }
                  handleSearch();
                }
              }}
            />
          </div>
        </div>
      </div>

      {/* Í≤∞Í≥º Ï†ïÎ≥¥ Î∞è Ï†ïÎ†¨ */}
      <div className="results-header">
        <div className="results-info">
          Ï¥ù {totalCount}Í±¥
        </div>
        <div className="sort-controls">
          <label>
            <input
              type="checkbox"
              checked={isDescending}
              onChange={handleSortToggle}
            />
            Ïó≠Ïàú Ï†ïÎ†¨
          </label>
        </div>
      </div>

      {/* ÌÖåÏù¥Î∏î */}
      <div className="table-container">
        <table className="inquiry-table">
          <thead>
            <tr>
              <th>Í≤¨Ï†ÅÎ≤àÌò∏</th>
              <th>ÌöåÏÇ¨Î™Ö</th>
              <th>Îã¥ÎãπÏûê</th>
              <th>ÏöîÏ≤≠ÏùºÏûê</th>
              <th>ÏÉÅÌÉú</th>
              <th>ÌîÑÎ°úÏ†ùÌä∏Î™Ö</th>
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr>
                <td colSpan={6} className="loading">Î°úÎî© Ï§ë...</td>
              </tr>
            ) : items.length === 0 ? (
              <tr>
                <td colSpan={6} className="no-data">Ï°∞ÌöåÎêú Îç∞Ïù¥ÌÑ∞Í∞Ä ÏóÜÏäµÎãàÎã§.</td>
              </tr>
            ) : (
              items.map((item) => (
                <tr 
                  key={item.tempEstimateNo}
                  onClick={() => handleRowClick(item)}
                  className="clickable-row"
                >
                  <td>{item.estimateNo}</td>
                  <td>{item.companyName}</td>
                  <td>{item.managerName || 'ÎØ∏ÏßÄÏ†ï'}</td>
                  <td>{item.requestDate ? formatDateYmd(item.requestDate) : extractDateFromTempEstimateNo(item.tempEstimateNo)}</td>
                  <td>
                    <span className={`status-badge status-${item.status}`}>
                      {item.statusText}
                    </span>
                  </td>
                  <td>{item.project}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      {/* ÌéòÏù¥Ïßï */}
      {totalPages > 1 && (
        <div className="pagination">
          <button
            onClick={() => handlePageChange(currentPage - 1)}
            disabled={currentPage === 1}
          >
            ‚Äπ
          </button>

          {Array.from({ length: Math.min(10, totalPages) }, (_, i) => {
            const startPage = Math.max(1, Math.min(currentPage - 5, totalPages - 9));
            const pageNum = startPage + i;

            if (pageNum > totalPages) return null;

            return (
              <button
                key={pageNum}
                onClick={() => handlePageChange(pageNum)}
                className={currentPage === pageNum ? 'active' : ''}
              >
                {pageNum}
              </button>
            );
          })}

          <button
            onClick={() => handlePageChange(currentPage + 1)}
            disabled={currentPage === totalPages}
          >
            ‚Ä∫
          </button>
        </div>
      )}
    </div>
  );
};

export default ExistingEstimateReInquiryPage;
